begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(11673486558897218543)
,p_plugin_type=>'REGION TYPE'
,p_name=>'APEX.HELLO.WORLD.CHART.SEMI.PROFESSIONAL'
,p_display_name=>'3. APEX Hello World Plug-in - apexchart.js - SEMI PROFESSIONAL with APEX_EXEC'
,p_supported_ui_types=>'DESKTOP'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','APEX.HELLO.WORLD.CHART.SEMI.PROFESSIONAL'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION F_RENDER (',
'    P_REGION                IN   APEX_PLUGIN.T_REGION,',
'    P_PLUGIN                IN   APEX_PLUGIN.T_PLUGIN,',
'    P_IS_PRINTER_FRIENDLY   IN   BOOLEAN',
') RETURN APEX_PLUGIN.T_REGION_RENDER_RESULT AS',
'',
'    /* Render result variable needed for return */',
'    VR_RENDER_RESULT APEX_PLUGIN.T_REGION_RENDER_RESULT;',
'    /* name of the label col from data source */',
'    C_X_COL CONSTANT APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_01%TYPE := P_REGION.ATTRIBUTE_01;',
'    /* name of the value col from data source */',
'    C_Y_COL CONSTANT APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_02%TYPE := P_REGION.ATTRIBUTE_02;',
'    /* height of the chart */',
'    C_CHART_HEIGHT CONSTANT APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_03%TYPE := APEX_ESCAPE.HTML_ATTRIBUTE(P_REGION.ATTRIBUTE_03);',
'    /* type of the chart */',
'    C_CHART_TYPE CONSTANT APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_04%TYPE := APEX_ESCAPE.HTML_ATTRIBUTE(P_REGION.ATTRIBUTE_04);',
'    /* Set if chart data is shown */',
'    C_SHOW_CHART_DATA CONSTANT APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_05%TYPE := P_REGION.ATTRIBUTE_05;',
'    /* Set if chart data is shown */',
'    VR_CHART_DATA_LABEL APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_06%TYPE := P_REGION.ATTRIBUTE_06;',
'    /* static if of the chart region (should be escaped for security) */',
'    C_STATIC_ID CONSTANT VARCHAR2(32767) := APEX_ESCAPE.HTML_ATTRIBUTE(P_REGION.STATIC_ID) || ''-container'';',
'    /* array of columns */',
'    VR_COLUMNS APEX_EXEC.T_COLUMNS;',
'    /* array of context */',
'    VR_CONTEXT APEX_EXEC.T_CONTEXT;',
'    /* number of label column in data source */',
'    VR_X_COL_NO PLS_INTEGER;',
'    /* number of value column in data source */',
'    VR_Y_COL_NO PLS_INTEGER;',
'    /* label variable */',
'    VR_X_VALUE VARCHAR2(32767);',
'    /* value variable */',
'    VR_Y_VALUE NUMBER;',
'    /* Store JSON Clob */',
'    VR_JSON VARCHAR2(32767);',
'BEGIN',
'',
'    /* load chart libary from plug-in */',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''apexcharts'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''apexchartjssrc''',
'    );',
'    ',
'    HTP.P(''<div id="''|| C_STATIC_ID ||''" class="apex-chart-js-container"></div>'');',
'    ',
'    /* execute query and store it to context array */',
'    VR_CONTEXT        := APEX_EXEC.OPEN_QUERY_CONTEXT(',
'        P_COLUMNS    => VR_COLUMNS,',
'        P_MAX_ROWS   => P_REGION.FETCHED_ROWS',
'    );',
'',
'    /* get position of label col */',
'    VR_X_COL_NO   := APEX_EXEC.GET_COLUMN_POSITION(',
'        VR_CONTEXT,',
'        C_X_COL',
'    );',
'    ',
'    /* get position of value col */',
'    VR_Y_COL_NO   := APEX_EXEC.GET_COLUMN_POSITION(',
'        VR_CONTEXT,',
'        C_Y_COL',
'    );',
'    ',
'    /* create data json for chart from cursor */',
'    /* USE APEX_JSON to prevent security issues with string concatination */',
'    APEX_JSON.INITIALIZE_CLOB_OUTPUT;',
'    ',
'    /* open json array - [ */',
'    APEX_JSON.OPEN_ARRAY;',
'    ',
'    /* loop through conext array */',
'    WHILE APEX_EXEC.NEXT_ROW(P_CONTEXT => VR_CONTEXT) LOOP',
'        /* get x value */',
'        VR_X_VALUE := APEX_EXEC.GET_VARCHAR2(',
'            VR_CONTEXT,',
'            VR_X_COL_NO',
'        );',
'       ',
'        /* get y value */',
'        VR_Y_VALUE   := APEX_EXEC.GET_NUMBER(',
'            VR_CONTEXT,',
'            VR_Y_COL_NO',
'        );',
'        ',
'        /* open json object - } */',
'        APEX_JSON.OPEN_OBJECT;',
'        ',
'        /* write x value to json */',
'        APEX_JSON.WRITE(''x'', VR_X_VALUE);',
'        ',
'        /* write y value to json */',
'        APEX_JSON.WRITE(''y'', VR_Y_VALUE);',
'        ',
'        /* Close json object - } */',
'        APEX_JSON.CLOSE_OBJECT;',
'    END LOOP;',
'    ',
'    /* open json array - ] */',
'    APEX_JSON.CLOSE_ARRAY;',
'    ',
'    /* don''t forget to cleanup */',
'    APEX_EXEC.CLOSE(VR_CONTEXT);',
'',
'    /* store created json on var */',
'    VR_JSON := APEX_JSON.GET_CLOB_OUTPUT(P_FREE => TRUE);',
'    ',
'    /* log produced json for debug */',
'    APEX_DEBUG.INFO(',
'        P_MESSAGE => ''JSON for chart'',',
'        P0 => VR_JSON',
'    );',
'    ',
'    /* add no data found message when json is empty */',
'    IF INSTR(VR_JSON,''{'') = 0 THEN',
'        HTP.P(''<span>''|| APEX_ESCAPE.HTML(P_REGION.NO_DATA_FOUND_MESSAGE)||''</span>'');',
'    ELSE',
'        /* add own javascript to render chart */',
'        /* because js code is string concatination all elements needs escaping or security check to make it impossible to add code */',
'        APEX_JAVASCRIPT.ADD_INLINE_CODE(',
'            P_CODE   => ''var options = {',
'                            chart: {',
'                                type: "''|| C_CHART_TYPE ||''", ',
'                                height: "''|| C_CHART_HEIGHT ||''"',
'                            },',
'                            series: [{',
'                                name: "Data",',
'                                data: '' || VR_JSON ||''',
'                            }]',
'                        };',
'                        var chart = new ApexCharts(document.querySelector("#''|| C_STATIC_ID ||''"), options);',
'                        chart.render();'',',
'            P_KEY    => ''chartRenderFunction''',
'        );',
'        IF C_SHOW_CHART_DATA = ''Y'' THEN',
'            /* escape label to precent xss attacks when set */',
'            IF P_REGION.ESCAPE_OUTPUT THEN',
'                VR_CHART_DATA_LABEL := APEX_ESCAPE.HTML(VR_CHART_DATA_LABEL);',
'            END IF;',
'    ',
'            HTP.P (''<b>''|| VR_CHART_DATA_LABEL ||'':</b> '' || VR_JSON);',
'        END IF;',
'    ',
'    END IF;',
'',
'    RETURN VR_RENDER_RESULT;',
'/* and DON''T forget exeption handling including also the cleanup */',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        APEX_EXEC.CLOSE(VR_CONTEXT);',
'        APEX_JSON.FREE_OUTPUT;',
'        RAISE;',
'END;'))
,p_api_version=>2
,p_render_function=>'F_RENDER'
,p_standard_attributes=>'SOURCE_LOCATION:FETCHED_ROWS:NO_DATA_FOUND_MESSAGE:ESCAPE_OUTPUT'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'1.0'
,p_about_url=>'https://github.com/RonnyWeiss/APEX-Hello-World-Plug-in'
,p_files_version=>7
);
